if (!defined('_PS_VERSION_'))
	exit;

/**
 * This file is used in the installDb() function and in the upgrade_module_3_1_1() function
 */
try
{

	$module = NewsletterPro::getInstance();

	$templates = array(
		'1' => array(
			'id' => 1,
			'css_style' => (
				version_compare(_PS_VERSION_, '1.6.0.1', '>=')
					? ''
					: ''
			)
		),
		'2' => array(
			'id' => 2,
			'css_style' => ''
		)
	);

	$backup = new NewsletterProBackupXml();
	$backup->load(subscription_template_install_db_3_1_1($templates), false);

	$xml_to_sql = new NewsletterProXMLToSQL($backup->getXML());

	foreach ($xml_to_sql->getSQL() as $table_name => $sql_array)
	{
		$table_name_np = preg_replace('/^'._DB_PREFIX_.'/', '', $table_name);

		if (!Db::getInstance()->execute('DELETE FROM `'.$table_name.'` WHERE 1'))
		{
			$module->addError(sprintf($module->l('Cannot drop data from the table "%s".'), $table_name));
			return false;
		}

		foreach ($sql_array as $sql)
		{

			if (!Db::getInstance()->execute($sql))
			{
				$module->addError(sprintf($module->l('Cannot insert the data into the table "%s".'), $table_name));
				return false;
			}
		}

		/* generate css files if the option Smarty Cache for CSS is activated */
		if ((int)Configuration::get('PS_CSS_THEME_CACHE'))
		{
			$results = Db::getInstance()->executeS('
				SELECT `id_newsletter_pro_subscription_tpl`, `name` FROM `'._DB_PREFIX_.'newsletter_pro_subscription_tpl`
			');

			foreach ($results as $row)
			{
				$sub_tpl_obj = new NewsletterProSubscriptionTpl((int)$row['id_newsletter_pro_subscription_tpl']);

				if (Validate::isLoadedObject($sub_tpl_obj))
				{
					foreach (Shop::getShops(false) as $shop)
					{
						$id_shop = (int)$shop['id_shop'];
						/* use hexToStr because the template don't have the css_style value at the moment of installation */
						$sub_tpl_obj->saveCSSStyleAsFile(NewsletterProTools::hexToStr($templates[$row['id_newsletter_pro_subscription_tpl']]['css_style']), $id_shop);
					}
				}
			}
		}

	}
}
catch(Exception $e)
{
	/* if there is an xml error */
	$module->addError($e->getMessage());
	return false;
}

